import React, { FC, ChangeEvent, useState } from "react";
import "./App.css";
import TodoTask from "./Components/TodoTask";
import { ITask } from "./Interfaces";
import Modal from './Modals/Modal';
import ModalContent from './Modals/ModalContent';
/* import { ReactComponent as Logo } from './Icons/Bin.svg'; */


const App: FC = () => {
  const [modalOpen, setModalOpen] = useState(false);
  const [task, setTask] = useState<string>("");

  const [todoList, setTodoList] = useState<ITask[]>([]);

  const handleChange = (event: ChangeEvent<HTMLInputElement>): void => {
    if (event.target.name === "task") {
      setTask(event.target.value);
    } 
  };

  const addTask = (): void => {
    const newTask = { taskName: task };
    setTodoList([...todoList, newTask]);
    setTask("");
  };

  const completeTask = (taskNameToDelete: string): void => {
    setTodoList(
      todoList.filter((task) => {
        return task.taskName != taskNameToDelete;
      })
    );
  };

  return (
    <div className="App">
      <div className="main">
        <h1>Titolo</h1>
        <div className="inputContainer">
          <input
            type="text"
            placeholder="Task..."
            name="task"
            value={task}
            onChange={handleChange}
          />
          <div className="todoList d-flex">

            {todoList.map((task: ITask, key: number) => {
              if (todoList == []) {
                return <p>non c'Ã¨ niente da vedere</p>
              }else{
                return   <TodoTask key={key} task={task} completeTask={completeTask} />;

              }
            })}
          </div>
        </div>

      </div>
      <div className="footer shadow">
      <Modal modalOpen={modalOpen}>
        <ModalContent setModalOpen={setModalOpen } />
      </Modal>

      <button
        type='button'
        className='labelMedium'
        onClick={() => setModalOpen(true)}
      > aggiungi nuova</button>
      <button className="labelMedium" onClick={addTask}>Add Task</button>


      </div>

    </div>
  );
};

export default App;
