import React from "react";
import { ITask } from "../Interfaces";
import { ReactComponent as Bin } from './../Icons/Bin.svg';
import { ReactComponent as Checkbox_Off } from './../Icons/Checkbox_Off.svg';
import { ReactComponent as Checkbox_On } from './../Icons/Checkbox_On.svg';

interface Props {
  task: ITask;
  deleteTask(taskName: string): void;
  checkTask(taskName: string): void;
}

const TodoTask = ({ task, deleteTask, checkTask }: Props) => {
  return (
    <div className="task d-flex">
      <Bin
      className="itemSpace"
        onClick={() => deleteTask(task.name)}
      />
      if {task.checked = false} {
        <Checkbox_Off/>
      }else{
        <Checkbox_On/>
      }
      <Checkbox_Off
      onChange={() => checkTask(task.name)} checked={task.checked}/>
      <input type="checkbox" onChange={() => checkTask(task.name)} checked={task.checked} />
      <div className="content">
        <span className="label cw">{task.name}</span>
      </div>
    </div>
  );
};

export default TodoTask;
